function send_order(e,t){jQuery(".article-comments-list").addClass("fetching"),jQuery.ajax({url:t,data:{comment_order:e},success:function(e){jQuery(".article-comments-list").html(e)},complete:function(){jQuery(".article-comments-list").removeClass("fetching")}})}jQuery(document).ready(function(){jQuery("#comment_order").change(function(){var e=window.location.href;send_order(this.value,e)})});