function verifyLoginLoad(){jQuery("#user_login").removeClass("available unavailable valid validated invalid checking").addClass("checking")}function verifyLoginAjax(e){verifyLoginLoad(),jQuery.get("/account/check_valid_name",{identifier:encodeURIComponent(e)},function(e){jQuery("#user_login").removeClass("checking"),jQuery("#url-check").html(e)})}jQuery(document).ready(function(){jQuery("#user_login").blur(function(){verifyLoginAjax(this.value)})});